mapper.select:
SELECT DTL_CT_NM
  FROM CATEGORY 
WHERE MST_CT_ID = #{mst_ct_id}

sql.where:
    	F.MST_CT_ID = C.MST_CT_ID 
		AND F.DTL_CT_ID = C.DTL_CT_ID                                            
		
			
				AND F.MST_CT_ID = #{mst_ct_id}
				AND C.DTL_CT_NM = #{dtl_ct_nm}
			
			
				AND F.MST_CT_ID = #{mst_ct_id}
			
			
				AND F.MST_CT_ID = #{mst_ct_id}
			
			
				AND F.MST_CT_ID = #{mst_ct_id}
