mapper.update:
		UPDATE users
		SET name = #{name}
		,password = #{password}
		,mail = #{mail}
		WHERE id = #{id}

mapper.select:
SELECT tt1.no,tt1.id, tt1.name,tt1.u_level,tt1.login,tt1.mail ,tt1.total_cnt FROM (SELECT t1.*, ROWNUM as no FROM (SELECT COUNT(*)over() AS total_cnt ,t0.* FROM users t0 ORDER BY reg_date ASC)t1
AND ((id LIKE '%'||#{SWord}||'%') OR (name LIKE '%'||#{SWord}||'%') OR (u_level LIKE '%'||#{SWord}||'%'))
AND id LIKE '%'||#{SWord}||'%'
AND name LIKE '%'||#{SWord}||'%'
AND u_level LIKE '%'||#{SWord}||'%'
)tt1 WHERE tt1.no BETWEEN #{start_NO} and #{end_NO}
