mapper.select:
SELECT ID
FROM USERS
WHERE NAME = #{name} 
	AND EMAIL = #{email}

SELECT PASSWORD
FROM USERS
WHERE ID = #{id} 
	AND EMAIL = #{email}

mapper.delete:
 			update USERS
				set delete_flag = 1
			where ID = #{id}

mapper.select:
SELECT COUNT(*) AS CNT
FROM USERS
WHERE ID = #{id}

SELECT COUNT(*) AS CNT
FROM USERS
WHERE ID = #{id}
AND   PASSWORD = #{password}

SELECT COUNT(*) AS CNT
FROM USERS
WHERE EMAIL = #{email}
