mapper.select:
SELECT ID
FROM USERS
WHERE NAME = #{NAME} 
	AND EMAIL = #{EMAIL}

SELECT PASSWORD
FROM USERS
WHERE ID = #{ID} 
	AND EMAIL = #{EMAIL}

mapper.delete:
 			update USERS
				set delete_flag = 1
			where ID = #{id}

mapper.select:
SELECT COUNT(*) AS CNT
FROM USERS
WHERE ID = #{id}

SELECT COUNT(*) AS CNT
